package leetcode

import "testing"

func TestLongestPalindrome(t *testing.T) {
	resultOfString("aba", longestPalindrome("babad"), t)
	resultOfString("bb", longestPalindrome("cbbd"), t)
	resultOfString("ABCBDABADBCBA", longestPalindrome("12ABCBDABADBCBA34"), t)
	resultOfString("bbb", longestPalindrome("bbb"), t)

	v := ""
	resultOfString("", longestPalindrome(v), t)
}
